,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE ',
'V_BLOB_OLD BLOB;',
'V_BLOB_NEW BLOB;',
'arr apex_application_global.vc_arr2;',
'arr_new apex_application_global.vc_arr2;',
'MIME_TYPE VARCHAR2(200);',
'FILENAME VARCHAR2(200);',
'v_length number;',
'MIME_TYPE_NEW VARCHAR2(200);',
'FILENAME_NEW VARCHAR2(200);',
'v_length_NEW number;',
'',
'begin',
'IF NOT APEX_COLLECTION.COLLECTION_EXISTS(''SPARE_DETAIL'') THEN',
'    APEX_COLLECTION.CREATE_COLLECTION(''SPARE_DETAIL'');',
'END IF;',
'',
'IF NOT APEX_COLLECTION.COLLECTION_EXISTS(''SPARE_DETAIL_NEW'') THEN',
'    APEX_COLLECTION.CREATE_COLLECTION(''SPARE_DETAIL_NEW'');',
'END IF;',
'',
'',
