wwv_flow_api.create_page_da_action(
 p_id=>wwv_flow_api.id(245333976808847858)
,p_event_id=>wwv_flow_api.id(245332903776847858)
,p_event_result=>'TRUE'
,p_action_sequence=>10
,p_execute_on_page_init=>'N'
,p_action=>'NATIVE_EXECUTE_PLSQL_CODE'
,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'Declare',
'  del_no        varchar2(30);',
'  del_date      date;',
'  nu_open       number;',
'  top_up        number;',
'  gen_hours     number;',
'  temp_previuos number;',
'  cur_gen_hours number;',
'cursor Y is ',
'	select  a.vc_site_code,a.vc_item_code, b.vc_item_desc, a.nu_serial_no, a.nu_tank_capacity,',
'	a.vc_gen_set_capacity,a.nu_opening_fuel_level,a.nu_cph',
'	from Maintain.dt_mst_site_item a, Maintain.mst_item b',
'	where a.vc_comp_code =b.vc_comp_code',
'	and a.vc_item_code =b.vc_item_code',
'	and a.vc_site_code =:P33_SITE_CODE_CODE;',
'Cursor C2 is select a.vc_item_code, b.vc_item_desc, ',
'a.vc_fuel_type_code, e.vc_item_desc fuel_name,',
'd.nu_balance_qty fuel_balance',
'from Maintain.dt_mst_site_item a, Maintain.mst_item b, ',
'Invent.mst_month_summary c, Invent.stock_summary d, Makess.mst_item e',
'where a.vc_comp_code =b.vc_comp_code',
'and a.vc_item_code =b.vc_item_code',
'and a.vc_comp_code =c.vc_comp_code',
'and a.vc_fuel_type_code =c.vc_item_code',
'and a.vc_comp_code =d.vc_comp_code',
'and a.vc_fuel_type_code =d.vc_item_code',
'and c.vc_comp_code =d.vc_comp_code',
'and c.vc_store_code  = d.vc_store_code',
'and a.vc_comp_code =e.vc_comp_code',
'and a.vc_fuel_type_code =e.vc_item_code',
'and a.vc_site_code =:P33_SITE_CODE_CODE',
'and c.vc_store_code =:P33_WAREHOUSE',
'and :P33_DELIVERY_DATE between c.dt_fin_start_date and c.dt_fin_end_date;',
'BEGIN',
'IF  APEX_COLLECTION.COLLECTION_EXISTS(''FUEL_DETAIL'') THEN',
'    APEX_COLLECTION.DELETE_COLLECTION(''FUEL_DETAIL'');',
'END IF;',
'IF NOT APEX_COLLECTION.COLLECTION_EXISTS(''FUEL_DETAIL'') then ',
'  APEX_COLLECTION.CREATE_COLLECTION(''FUEL_DETAIL'');',
'END IF;',
'',
'',
'for J in Y',
'loop',
'begin ',
'select nvl(nu_fuel_after_top_up,0),nu_current_gen_hours  ',
'into top_up,cur_gen_hours',
'from Maintain.dt_fuel_del',
'where vc_comp_code =:global_comp_code',
'and vc_site_code =J.vc_site_code',
'and vc_serial_no =J.nu_serial_no',
'and vc_item_code =J.vc_item_code',
'and ((to_number(substr(vc_delivery_no,5))),to_date(dt_delivery_date)) =',
' (select nvl(max(to_number(substr(vc_delivery_no,5))),0), max(to_date(dt_delivery_date))',
' from Maintain.dt_fuel_del',
' where vc_comp_code =:global_comp_code',
' and vc_site_code =J.vc_site_code',
' and vc_serial_no =J.nu_serial_no',
' and vc_item_code =J.vc_item_code',
' );',
'exception',
'when others then',
'top_up:=''0'';',
'end;',
'',
'begin',
'select nvl(nu_opening_fuel_level,0),nvl(nu_max_gen_hours,0)',
'into nu_open,gen_hours',
'from Maintain.dt_mst_site_item',
'where vc_comp_code =:global_comp_code',
'and vc_site_code =J.vc_site_code',
'and nu_serial_no =J.nu_serial_no;',
'exception',
'when others then',
'nu_open:=''0'';',
'end;',
'if top_up <= 0 then	',
'temp_previuos:=gen_hours;',
'end if; ',
'if top_up > 0 then	',
'temp_previuos:=cur_gen_hours;',
'end if;',
' APEX_COLLECTION.ADD_MEMBER(',
'    p_COLLECTION_NAME => ''FUEL_DETAIL'',',
'    p_c001       => J.vc_site_code,',
'    p_c002       => J.vc_item_code,',
'    p_c003       => J.vc_item_desc,',
'    P_c004       => J.nu_serial_no,',
'    P_c005       => J.nu_tank_capacity,',
'    P_c006       => J.nu_opening_fuel_level,',
'    P_c007       => NULL,',
'    P_c008       => NULL,',
'    P_c009       => NULL,',
'    P_c010       => NULL,',
'    P_c011       => J.vc_gen_set_capacity,',
'    P_c012       => temp_previuos,',
'    P_c013       => NULL,',
'    P_c014       => NULL,',
'    P_c015       => J.nu_cph',
'    );',
'    ',
'end loop;',
'end;',
'',
'',
''))
,p_attribute_02=>'P33_SITE_CODE,P33_WAREHOUSE,P33_DELIVERY_DATE'
,p_attribute_05=>'PLSQL'
,p_wait_for_result=>'Y'
);
