'begin',
'SELECT ',
'a.vc_internal_ticket_no vc_internal_ticket_no,',
'a.vc_incidence_ticket_no,',
'a.dt_ticket_date, ',
'a.nu_customer_code,',
'b.vc_customer_name,',
'a.vc_site_code , ',
'c.vc_site_desc, ',
'a.vc_fault_code, ',
'd.vc_fault_description, ',
'a.vc_assign_to',
'',
'Into  ',
'',
':P112_INTERNAL_TICKET_NO,',
':P112_INCIDENT_TICKET_NO,',
':P112_TICKET_DATE,',
':P112_CUSTOMER_CODE,',
':P112_CUSTOMER_NAME,',
':P112_SITE_CODE,',
':P112_SITE_NAME,',
':P112_FAULT_CODE,',
':P112_FAULT_NAME,',
':P112_ASSIGN_TO',
'',
