'and a.nu_customer_code = b.nu_customer_code',
'and a.vc_comp_code = c.vc_comp_code',
'and a.vc_site_code = c.vc_site_code',
'and a.vc_comp_code = d.vc_comp_code',
'and a.vc_fault_code = d.vc_fault_code',
'and a.vc_comp_code = :GLOBAL_COMP_CODE',
'AND a.vc_internal_ticket_no = :P112_INTERNAL_TICKET_NO ;',
'exception when no_data_found then ',
'null;',
